  /**
   * Close the lock_connection to unlock.
   *
   * @param {any} cb Callback called after disconnect.
   * @returns {Function} callback
   *
   * @memberOf Container
   */
  unlock(cb){
    /**
     * Function wrapped for async
     * @param {Function} callback Callback Function
     * @returns {undefined}
     */
    const fn = (callback) => {
      this.lock_connection.close();
      return callback();
    };

    if (cb){
      fn(cb);
    } else {
      return fn;
    }
  }

