'use strict';

const Path = require('./Path.js');
/**
 * Representation of a file path
 * A file path can never be empty or terminated with a slash.
 * Examples: "file.suffix", "dir1/dir2/file.suffix"
 * @extends Path
 */
class File extends Path {
  /**
   *
   * @param {string} path A path to a file.
   * @throws Throws an error if the passed path ends with a '/' or is empty, since those paths are folders and not files.
   */
  constructor(path) {
    super(path);

    if (path === '' || path.endsWith('/')) {
      throw new Error(`${path  } is a folder`);
    }
  }
}

module.exports = File;