'use strict';

const PathWithContent = require('./PathWithContent.js');
/**
 * Representation of a file for writing it to the HDI work filesystem
 * @extends PathWithContent
 */
class FileWithContent extends PathWithContent {
  /**
   * @param {string} path A path to a file.
   * @param {ReadStream | Buffer} content Content of the file.
   * @throws Throws an error if the passed path ends with a '/' or is empty, since those paths are folders and not files.
   */
  constructor(path, content) {
    super(path, content);

    if (path === '' || path.endsWith('/')) {
      throw new Error(`${path  } is a folder`);
    }
  }
}


module.exports = FileWithContent;