'use strict';

const PathWithContent = require('./PathWithContent.js');

/**
 * Representation of a folder for writing it to the HDI work filesystem.
 * @extends PathWithContent
 */
class FolderWithContent extends PathWithContent {
  /**
   * @param {string} path Path to a folder.
   * @throws Throws an error if the passed path does not end with a '/' or is empty, since those paths are files and not folders.
   */
  constructor(path) {
    // (data) content of folders is always empty
    super(path, undefined);

    if (!(path === '' || path.endsWith('/'))) {
      throw new Error(`${path  } is a file`);
    }
  }
}

module.exports = FolderWithContent;