'use strict';
/**
 * Return only the last result of an async series.
 *
 * @param {any} callback Actual callback you want to call.
 * @returns {CallbackFunction} Return function taking error, results as parameters.
 */
module.exports = function(callback){
  return function(async_series_error, async_series_results){
    if (async_series_error){
      return callback(async_series_error);
    } else if (async_series_results.length > 0){
      return callback(null, async_series_results[async_series_results.length - 1]);
    } else {
      return callback(async_series_error, async_series_results);
    }
  };
};