exports.REGEXP_COMBINING_MARKS = REGEXP_COMBINING_MARKS;
exports.REGEXP_CONVERSION_SPECIFICATION = REGEXP_CONVERSION_SPECIFICATION;
exports.REGEXP_DIGIT = REGEXP_DIGIT;
exports.REGEXP_HTML_SPECIAL_CHARACTERS = REGEXP_HTML_SPECIAL_CHARACTERS;
exports.REGEXP_NON_LATIN = REGEXP_NON_LATIN;
exports.REGEXP_SPECIAL_CHARACTERS = REGEXP_SPECIAL_CHARACTERS;
